import React, { useState, useEffect } from 'react';
import {
  List,
  Datagrid,
  TextField,
  Show,
  SimpleShowLayout
} from 'react-admin';
import { useDataProvider, Loading, Error } from 'react-admin';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

const Comment = ({ record }) => {
  const dataProvider = useDataProvider();
  const [total, setTotal] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  useEffect(() => {
      dataProvider.getList('comments', { pagination: { page: 1 , perPage: 100000 }, sort: {}, filter: {voice_doc_id: record.id} })
          .then(({ data, total }) => {
              setTotal(total);                
              setLoading(false);
          })
          .catch(error => {
              setError(error);
              setLoading(false);
          })
  }, []);

  var comments = {
    result: total + "  Comments"
  }

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!total) return <TextField record={comments} source="result" />;

  return (
    <TextField record={comments} source="result" />
  )
};

const reports = {
  no: "No Report",
  yes: "Reported by user"
}

const ConditionBooleanField = ({ record, ...rest }) => {
    return record.report_users
        ? <TextField record={reports} source="yes" />
        : <TextField record={reports} source="no" />;
}

ConditionBooleanField.defaultProps = {
  addLabel: true,
};

const Player = ({ record }) => (
  <audio controls src={record.voice_url} />
);

Player.defaultProps = {
  addLabel: true,
};

const CommentGrid = ({ record }) => {

  const dataProvider = useDataProvider();
  const [total, setTotal] = useState();
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  const comments = {
    no: "No Comment"
  }
  useEffect(() => {
      dataProvider.getList('comments', { pagination: { page: 1 , perPage: 100000 }, sort: {}, filter: {voice_doc_id: record.id} })
          .then(({ data, total }) => {
              setTotal(total);  
              setData(data);
              setLoading(false);
          })
          .catch(error => {
              setError(error);
              setLoading(false);
          })
  }, []);

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!total) return <TextField record={comments} source="no" />;
  if (!data) return [];

  return (
    <Table>
      <TableHead>
        <TableRow>
          <TableCell>Address</TableCell>
          <TableCell>Voice</TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        {data.map(value => {
          return (
            <TableRow key={value.created_timestamp}>
              <TableCell>{value.address}</TableCell>
              <TableCell><audio controls src={value.voice_url} /></TableCell>
            </TableRow>
          )
        })}
      </TableBody>
    </Table>
  );
};

CommentGrid.defaultProps = {
  addLabel: true,
};

export const VoiceList = props => (
  <List {...props} >
    <Datagrid rowClick="show">
      <TextField label="Location" source="address" />
      <Comment label="Comments" source="id" />
      <Player label="Voice" source="voice_url" />
      <ConditionBooleanField source="report_users" />
    </Datagrid>
  </List>
);

export const VoiceShow = props => (
  <Show {...props}>
    <SimpleShowLayout>
        <TextField source="address" />
        <Player label="Voice" source="voice_url" />
        <CommentGrid label="Comments" source="id" />
        <ConditionBooleanField source="report_users" />
    </SimpleShowLayout>
  </Show>
);
