import React from 'react';
import {
  List,
  Show,
  SimpleShowLayout,
  Datagrid,
  TextField,
  RichTextField
} from 'react-admin';

export const SupportList = props => (
  <List {...props}>
    <Datagrid rowClick="show">
      <TextField source="title" />
      <TextField source="message" />
      <TextField source="email" />
    </Datagrid>
  </List>
);

export const SupportShow = (props) => (
    <Show {...props}>
        <SimpleShowLayout>
            <TextField source="title" />
            <RichTextField source="message" />
            <TextField source="email" />
        </SimpleShowLayout>
    </Show>
);
