import React, { useState, useEffect } from 'react';
import {
  List,
  Datagrid,
  TextField,
  EditButton
} from 'react-admin';
import { useDataProvider, Loading, Error } from 'react-admin';

const Comment = ({ voiceId }) => {
    const dataProvider = useDataProvider();
    const [total, setTotal] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();
    useEffect(() => {
        dataProvider.getList('comments', { id: voiceId })
            .then(({ data, total }) => {
                setTotal(total);                
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            })
    }, []);

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!total) return 0;

  return (
    <span>{total} comments</span>
  )
};

const ConditionBooleanField = ({ record, ...rest }) => {
    return record.report_users
        ? <span>Reported by user</span>
        : <span>No Report</span>;
}

export const VoiceList = props => (
  <List {...props}>
    <Datagrid>
      <TextField label="Location" source="address" />
      <ConditionBooleanField source="report_users" />
      <TextField source="comments" />
      <Comment source="id" />
    </Datagrid>
  </List>
);
