import React, { useState, useEffect } from 'react';
import {
  List,
  Datagrid,
  TextField,
  Show,
  SimpleShowLayout
} from 'react-admin';
import { useDataProvider, Loading, Error } from 'react-admin';

const Comment = ({ record }) => {
    const dataProvider = useDataProvider();
    const [total, setTotal] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();
    useEffect(() => {
        dataProvider.getList('comments', { pagination: { page: 1 , perPage: 100000 }, sort: {}, filter: {voice_doc_id: record.id} })
            .then(({ data, total }) => {
                setTotal(total);                
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            })
    }, []);

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!total) return 0;

  return (
    <span>{total} comments</span>
  )
};

const ConditionBooleanField = ({ record, ...rest }) => {
    return record.report_users
        ? <span><br />Reported by user</span>
        : <span><br />No Report</span>;
}

const CommentList = ({ record }) => {
  const dataProvider = useDataProvider();
  const [total, setTotal] = useState();
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  useEffect(() => {
      dataProvider.getList('comments', { pagination: { page: 1 , perPage: 100000 }, sort: {}, filter: {voice_doc_id: record.id} })
          .then(({ data, total }) => {
              setTotal(total);
              setData(data);
              setLoading(false);
          })
          .catch(error => {
              setError(error);
              setLoading(false);
          })
  }, []);

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!data) return null;
  if (!total) return 0;

  var result = [];

  data.forEach(item => {
    result.push(<span>{item.address}</span>)
  })

  return (
    result
  )
};

export const VoiceList = props => (
  <List {...props} >
    <Datagrid rowClick="show">
      <TextField label="Location" source="address" />
      <Comment label="Comments" source="id" />
      <ConditionBooleanField source="report_users" />
    </Datagrid>
  </List>
);

export const VoiceShow = props => (
  <Show {...props}>
    <SimpleShowLayout>
        <TextField source="address" />
        <CommentList label="Comments" source="id" />
        <ConditionBooleanField source="report_users" />
    </SimpleShowLayout>
  </Show>
);
