import React from 'react';
import {
  List,
  Edit,
  Create,
  Datagrid,
  ReferenceField,
  TextField,
  EditButton,
  ReferenceInput,
  SelectInput,
  DateTimeInput,
  DateField,
  SimpleForm,
  TextInput
} from 'react-admin';
import { URLField } from '../components';

export const MessageList = props => (
  <List {...props}>
    <Datagrid>
      <ReferenceField label="Group" source="group.id" reference="groups" allowEmpty>
        <TextField source="name" />
      </ReferenceField>
      <TextField source="title" />
      <DateField source="date" showTime />
      <TextField label="Message" source="body" />
      <URLField label="Audio" source="audio_url" />
      <URLField label="Video" source="video_url" />
      <EditButton />
    </Datagrid>
  </List>
);

const dateParser = v => {
  // var timestamp = new Date(v).getTime();
  // return timestamp;
  return v;
};

export const MessageEdit = props => (
  <Edit {...props}>
    <SimpleForm redirect="/messages" >
      <ReferenceInput label="Group" source="group.id" reference="groups">
        <SelectInput optionText="name" />
      </ReferenceInput>
      <TextInput source="title" />
      <DateTimeInput source="date" parse={dateParser} />
      <TextInput label="Message" multiline source="body" toolbar={[]} />
      <TextInput label="Audio" source="audio_url" type="url" />
      <TextInput label="Video" source="video_url" type="url" />
    </SimpleForm>
  </Edit>
);

export const MessageCreate = props => (
  <Create {...props}>
    <SimpleForm redirect="/messages" >
      <ReferenceInput label="Group" source="group.id" reference="groups" allowEmpty>
        <SelectInput optionText="name" />
      </ReferenceInput>
      <TextInput source="title" />
      <DateTimeInput source="date" parse={dateParser} />
      <TextInput label="Message" multiline source="body" toolbar={[]} />
      <TextInput label="Audio" source="audio_url" type="url" />
      <TextInput label="Video" source="video_url" type="url" />
    </SimpleForm>
  </Create>
);
