import React, { useState, useEffect } from 'react';
import {
  List,
  Datagrid,
  TextField,
  DateField,
  Show,
  SimpleShowLayout
} from 'react-admin';
import { useDataProvider, Loading, Error } from 'react-admin';
// import { List as MyList } from '@material-ui/core/List';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import PersonIcon from '@material-ui/core/Avatar';
import AudioPlayer from "react-h5-audio-player";


const Comment = ({ record }) => {
  const dataProvider = useDataProvider();
  const [total, setTotal] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  useEffect(() => {
      dataProvider.getList('comments', { pagination: { page: 1 , perPage: 100000 }, sort: {}, filter: {voice_doc_id: record.id} })
          .then(({ data, total }) => {
              setTotal(total);                
              setLoading(false);
          })
          .catch(error => {
              setError(error);
              setLoading(false);
          })
  }, []);

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!total) return 0;

  return (
    <span>{total} comments</span>
  )
};

const ConditionBooleanField = ({ record, ...rest }) => {
    return record.report_users
        ? <span><br />Reported by user</span>
        : <span><br />No Report</span>;
}

const cardStyle = {
  width: '100%',
  margin: '0.5em',
  display: 'inline-block',
  verticalAlign: 'top'
};

const titleStyle = {
  width: '100%',
  display: 'inline-block',
  fontSize: '15px',
  verticalAlign: 'top'
};

const Player = ({ record }) => (
  <AudioPlayer
    src={record.voice_url}
  />
);

const CommentGrid = ({ record }) => {

  const dataProvider = useDataProvider();
  const [total, setTotal] = useState();
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  useEffect(() => {
      dataProvider.getList('comments', { pagination: { page: 1 , perPage: 100000 }, sort: {}, filter: {voice_doc_id: record.id} })
          .then(({ data, total }) => {
              setTotal(total);  
              setData(data);
              setLoading(false);
          })
          .catch(error => {
              setError(error);
              setLoading(false);
          })
  }, []);

  if (loading) return <Loading />;
  if (error) return <Error />;
  if (!total) return 0;
  if (!data) return [];

  return (
    <div style={{ margin: '1em' }}>
    {data.map(value => {
      console.log(value.created_timestamp);
      return (
        <Card key={value.created_timestamp} style={cardStyle}>
            <CardContent>
                <span style={titleStyle} >{value.address} </span>
            </CardContent>
        </Card>
      )
    })}
    </div>
  )
};

export const VoiceList = props => (
  <List {...props} >
    <Datagrid rowClick="show">
      <TextField label="Location" source="address" />
      <Comment label="Comments" source="id" />
      <Player source="voice_url" />
      <ConditionBooleanField source="report_users" />
    </Datagrid>
  </List>
);

export const VoiceShow = props => (
  <Show {...props}>
    <SimpleShowLayout>
        <TextField source="address" />
        <CommentGrid label="Comments" source="id" />
        <ConditionBooleanField source="report_users" />
    </SimpleShowLayout>
  </Show>
);
