import React from 'react';

import { Admin, Resource } from 'react-admin';
// import { RestProvider, AuthProvider, base64Uploader } from 'ra-data-firestore-client';
import { RestProvider, AuthProvider } from './lib'

import { UserList, UserCreate, UserEdit } from './models';
import { GroupList, GroupCreate, GroupEdit } from './models';
import { MessageList, MessageCreate, MessageEdit } from './models';
import { QuestionList, QuestionCreate, QuestionEdit } from './models';
import { InstructionList, InstructionCreate, InstructionEdit } from './models';
import { SupportList, SupportShow } from './models';
import { ResearcherList, ResearcherCreate, ResearcherEdit } from './models'

const firebaseConfig = {
  apiKey: 'AIzaSyCz6-jatuhDRccH3_XdnLiCPqmNXSc5JdQ',
  authDomain: 'nudge-ea071.firebaseapp.com',
  databaseURL: 'https://nudge-ea071.firebaseio.com',
  projectId: 'nudge-ea071',
  storageBucket: 'nudge-ea071.appspot.com',
  messagingSenderId: '243008410342',
  appId: '1:243008410342:web:b569504fbb555b5e3b6127'
};
const trackedResources = [{ name: 'users' },
                          { name: 'researchers' },
                          { name: 'groups' },
                          { name: 'messages', uploadFields: ['image_file', 'audio_file'] },
                          { name: 'questions' },
                          { name: 'instructions' },
                          { name: 'supports'}];

const authConfig = {
  userProfilePath: '/users/',
  userAdminProp: 'isAdmin'
};

// const dataProvider = base64Uploader(RestProvider(firebaseConfig, { trackedResources }));
const dataProvider = RestProvider(firebaseConfig, { trackedResources });
const App = () => (
  <Admin dataProvider={dataProvider} authProvider={AuthProvider(authConfig)}>
    <Resource name="users" list={UserList} edit={ResearcherEdit} create={ResearcherCreate} />
    <Resource name="researchers" list={ResearcherList} edit={UserEdit} create={UserCreate} />
    <Resource name="groups" list={GroupList} edit={GroupEdit} create={GroupCreate} />
    <Resource name="messages" list={MessageList} edit={MessageEdit} create={MessageCreate} />
    <Resource name="questions" list={QuestionList} edit={QuestionEdit} create={QuestionCreate} />
    <Resource name="instructions" list={InstructionList} edit={InstructionEdit} create={InstructionCreate} />
    <Resource name="supports" list={SupportList} show={SupportShow} />
  </Admin>
);

export default App;
